document.addEventListener("DOMContentLoaded",()=>{function stopLoading(){let a=document.querySelector(".single__mainContent.article-loaded");let b=document.querySelector(".foxyLong");if(b.getBoundingClientRect().top-a.getBoundingClientRect().bottom>150){a.insertAdjacentHTML("beforeend",`\n            <div id="stop" style='height: ${b.getBoundingClientRect().top-a.getBoundingClientRect().bottom}px' class="tester" role="img" ></div>`);let stop=document.querySelector("#stop");stop.addEventListener("click",()=>{window.scrollTo({top:b.offsetTop,behavior:"smooth"})})}}stopLoading()});